mapper.select:
SELECT PNO, PNAME, CONTENT, STA_DATE, END_DATE, STATE, CRE_DATE, TAGS
FROM PROJECTS
WHERE PNO = #{value}

mapper.update:
UPDATE PROJECTS
SET PNAME = #{title}
	,CONTENT = #{content}
	,STA_DATE = #{startDate}
	,END_DATE = #{endDate}
	,STATE = #{state}
	,TAGS = #{tags}
WHERE PNO = #{no}
